import React, { useState } from 'react';
import './App.css';
import FileUpload from './FileUpload';
import SearchBar from './SearchBar';
import AnswerDisplay from './AnswerDisplay';
import axios from 'axios';

var fileDownload = require('js-file-download');

function App() {
  const [file, setFile] = useState(null);
  const [answers, setAnswers] = useState('');


   const handlePDFDownload = () => {
        axios.get('api/endpoint/to/view/', { 
            responseType: 'blob',
        }).then(res => {
            fileDownload(res.data, 'filename.pdf');
            console.log(res);
        }).catch(err => {
            console.log(err);
        })
      }

  const handleSearch = (searchTerm) => {
    // Perform search logic here
    // For demonstration, let's just set the answers randomly
    const randomAnswer = Math.random() > 0.5 ? 'Yes' : 'No';
    setAnswers(randomAnswer);
  };

  return (
    <div className="container">
      <h1 className="title">Social Justice Tool</h1>
      <FileUpload onFileChange={handlePDFDownload} />
      <SearchBar onSearch={handleSearch} />
      <AnswerDisplay answers={answers} />
    </div>
  );
}

export default App;
