import React, { useState } from 'react';
import './App.css';
import FileUpload from './FileUpload';
import SearchBar from './SearchBar';
import AnswerDisplay from './AnswerDisplay';


const backendUrl = process.env.REACT_APP_BACKEND_URL;
console.log(process.env);

function App() {
  const [file, setFile] = useState(null);
  const [answers, setAnswers] = useState('');

  const handleFileChange = (selectedFile) => {
    setFile(selectedFile);
  };

 const handleUpload = () => {
  // Create a FormData object and append the file to it
  const formData = new FormData();
  formData.append('file', file);

  // Call the backend Django view to upload the file
  fetch('${backendUrl}/api/upload/', {
    method: 'POST',
    body: formData, // Use FormData object instead of directly passing the file
    // Add any additional headers if needed
  })
  .then(response => {
    // Handle response from backend
    console.log('File uploaded successfully');
  })
  .catch(error => {
    // Handle error
    console.error('Error uploading file:', error);
  });
};


  const handleSearch = (searchTerm) => {
    const randomAnswer = Math.random() > 0.5 ? 'Yes' : 'No';
    setAnswers(randomAnswer);
  };

  return (
    <div className="container">
      <h1 className="title">Social Justice Tool</h1>
      <FileUpload onFileChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>
      <SearchBar onSearch={handleSearch} />
      <AnswerDisplay answers={answers} />
    </div>
  );
}

export default App;
