import React, { useState } from 'react';
import './App.css'; // Import CSS file for styling

function App() {
  const [file, setFile] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [answers, setAnswers] = useState('');

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    setFile(selectedFile);
  };

  const handleSearch = () => {
    // Perform search logic here
    // For demonstration, let's just set the answers randomly
    const randomAnswer = Math.random() > 0.5 ? 'Yes' : 'No';
    setAnswers(randomAnswer);
  };

  return (
    <div className="container">
      <h1 className="title">File Upload, Search Bar, and Answers Display</h1>
      <div className="input-container">
        <input type="file" onChange={handleFileChange} />
      </div>
      <div className="input-container">
        <input
          type="text"
          placeholder="Search..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button className="search-button" onClick={handleSearch}>Search</button>
      </div>
      <div className="textarea-container">
        <textarea
          rows="4"
          cols="50"
          value={answers}
          readOnly
          className="answers-textarea"
        />
      </div>
    </div>
  );
}

export default App;
