import React, { useState } from 'react';
import './App.css';
import FileUpload from './FileUpload';
import SearchBar from './SearchBar';
import AnswerDisplay from './AnswerDisplay';
import axios from 'axios';

var fileDownload = require('js-file-download');

function App() {
  const [file, setFile] = useState(null);
  const [answers, setAnswers] = useState('');

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleFileUpload = () => {
    const formData = new FormData();
    formData.append('file', file);

    axios.post('http://your-django-backend/api/upload/', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    }).then(response => {
      console.log(response);
      alert('File uploaded successfully!');
    }).catch(error => {
      console.error('Error uploading file:', error);
      alert('Error uploading file. Please try again.');
    });
  };


  const handleSearch = (searchTerm) => {
    // Perform search logic here
    // For demonstration, let's just set the answers randomly
    const randomAnswer = Math.random() > 0.5 ? 'Yes' : 'No';
    setAnswers(randomAnswer);
  };

  return (
    <div className="container">
      <h1 className="title">Social Justice Tool</h1>
